#include <stdio.h>
#include <string.h>
#include "libeip1962.h"

int hex2bin(char *source_str, char *dest_buffer)
{
  char *line = source_str;
  char *data = line;
  int offset;
  int read_byte;
  int data_len = 0;

  while (sscanf(data, " %02x%n", &read_byte, &offset) == 1)
  {
    dest_buffer[data_len++] = read_byte;
    data += offset;
  }
  return data_len;
}

int main()
{
  char buf[] = "";
  char str[] = "";
  int len = hex2bin(str, buf);
  GoSlice in = {buf, len, len};
  struct Run_return res;
  res = Run(buf, len);
  if(res.r0 == 0)
  {
    printf("err: %s\n", (res.r1));
    return 1;
  }
  return 0;
}